require.config({
	baseUrl: '../node_modules/',
	paths: {
		angular: 'angular/angular.min',
		jquery: 'jquery-1x/dist/jquery.min',
		angular_aria: 'angular-aria/angular-aria.min',
		angular_animate: 'angular-animate/angular-animate.min',
		angular_route: 'angular-route/angular-route.min',
		angular_busy: 'angular-busy/dist/angular-busy.min',
		angular_toastr: 'angular-toastr/dist/angular-toastr.tpls.min',
		angular_async_loader: 'angular-async-loader/angular-async-loader.min',
		angular_carousel: 'angular-carousel/angular-carousel.min',
		angular_dialog_service: 'angular-dialog-service/dist/dialogs.min',
		angular_loading_bar: 'angular-loading-bar/build/loading-bar.min',
		angular_material: 'angular-material/angular-material.min',
		angular_material_datetimepicker: 'angular-material-datetimepicker-zz/js/angular-material-datetimepicker.min',
		locale_zh_cn: 'angular-i18n/angular-locale_zh-cn',
		ui_bootstrap: 'angular-ui-bootstrap/dist/ui-bootstrap-tpls',
		ui_router: 'angular-ui-router/release/angular-ui-router.min',
		ui_bootstrap_datetimepicker: 'angular-ui-bootstrap-datetimepicker/datetimepicker',
		bootstrap: 'bootstrap/dist/js/bootstrap.min',
		Home_DemoIndex: '../Public/controller/Home/DemoIndex'
	},
	map: {
		'*': {
			'css': 'require-css/css'
		}
	},
	shim: {
		angular: {
			exports: 'angular'
		},
		angular_i18n: {
			deps:['angular']
		},
		angular_animate: {
			deps:['angular'],
			exports: 'angular_animate'
		},
		angular_busy: {
			deps:['angular'],
			exports: 'angular_busy'
		},
		angular_toastr: {
			deps:['angular', 'angular_animate'],
			exports: 'angular_animate'
		},
		angular_route: {
			deps:['angular'],
			exports: 'angular_route'
		},
		angular_material: {
			deps:['angular', 'angular_aria']
		},
		angular_material_datetimepicker:{
			deps: ['angular', 'angular_aria', 'angular_material', 'angular_material_datetimepicker']
		},
		ui_bootstrap: {
			deps:['angular'],
			exports: 'ui_bootstrap'
		},
		ui_bootstrap_datetimepicker: {
			deps:['angular', 'ui_bootstrap']
		},
		ui_router: {
			deps:['angular'],
			exports: 'ui_router'
		},
		bootstrap: {
			deps:['jquery'],
			exports: 'bootstrap'
		},
		jquery: {
			exports: 'jquery'
		}
	},
	//deps: 'controller',
	urlArgs: "bust=" + (new Date()).getTime()  //防止读取缓存，调试用
});
require(['jquery', 'angular', 'bootstrap', 'angular_busy', 'angular_toastr', '../Public/app-routes'], function ($, angular) {
	angular.element(document).ready(function () {
		angular.element(document).find('html').addClass('ng-app');
		angular.bootstrap(document, ['starter']);
	});
});